document.querySelectorAll(".product-item__description-btn").forEach(e=>{e.addEventListener("click",(function(t){t.preventDefault(),document.querySelectorAll(".product-item__description-btn").forEach(e=>{e.classList.remove("active")}),e.classList.add("active"),document.querySelectorAll(".product-item__description-item").forEach(e=>{e.classList.contains("active")&&e.classList.remove("active")});const n=this.href.slice(this.href.indexOf("#")+1);document.getElementById(n).classList.add("active")}))});const productCounterLess=document.getElementById("productCounterLess"),productCounterMore=document.getElementById("productCounterMore"),productCounter=document.getElementById("productCounter");function sub(){2==+productCounter.innerText?(productCounterLess.style.display="none",productCounter.innerText=+productCounter.innerText-1):+productCounter.innerText>1&&(productCounter.innerText=+productCounter.innerText-1)}function add(){productCounterLess.style.display="block",productCounter.innerText=+productCounter.innerText+1}productCounterLess.addEventListener("click",sub),productCounterMore.addEventListener("click",add);